Fleet.query:
fragment VehicleFields on token_metadata    
{    
    image    
    name    
    podium_places    
    races_raced    
    token_id    
    wins    
    __typename   
    dynamicStats: attributes    
    {   
        max_capacity   
        max_range   
        max_speed   
        fuel_efficiency   
        emission_rate   
        __typename   
    }   
    dynamicAttributes: attributes   
    {   
        background_color   
        slogan   
        sign   
        __typename   
    }   
    staticAttributes: attributes   
    {   
        animal   
        color   
        food   
        mention   
        other   
        transportation_mode   
        vehicle_type   
        __typename   
    }   

 }    
 query Fleet( $address: String!, $classes: [String!], $sortBy: [token_metadata_order_by!] )    
 {   
        token_metadata(    
            where:    
            {    
                token:    
                {    
                    address:    
                    {   
                        _regex: $address   
                    }    
                }, 
                attributes:    
                {    
                    transportation_mode:    
                    {    
                        _in: $classes   
                    }    
                }    
            }    
          order_by: $sortBy    
        )    
        {    
            ...VehicleFields    
            __typename    
        }    
}

