fragment VehicleFields on token_metadata {
  token_id
  name
  image
  wins
  podium_places
  races_raced
  __typename
}

fragment VehicleStats on token_metadata {
  stats: attributes {
    max_capacity
    max_range
    max_speed
    fuel_efficiency
    emission_rate
    __typename
  }
  __typename
}

fragment RaceFields on races {
  id
  name
  class
  distance
  weather
  terrain
  weight
  cargo
  fromCoord
  toCoord
  prize_pool
  entry_fee
 start
  sponsor
  promo_link
  promo_banner
  participants {
    racer {
      address
      username
      __typename
    }
    vehicle {
      ...VehicleStats
      ...VehicleFields
      __typename
    }
    __typename
  }
  results {
    time
    place
    prize
    racer {
      address
      username
      __typename
    }
    vehicle {
      ...VehicleStats
      ...VehicleFields
      __typename
    }
    __typename
  }
  participants_aggregate {
    aggregate {
      count
      __typename
    }
    __typename
  }
  results_aggregate {
    aggregate {
      count
      __typename
    }
    __typename
  }
  __typename
}

query NextToRaceRaces($raceClasses: String_comparison_exp) {
  races(
    where: {status: {_eq: "next_to_race"}, class: $raceClasses}
    order_by: {start: asc}
    limit: 50
  ) {
    ...RaceFields
    __typename
  }
}

Racers.query:
query Racers($address: String!) {
  racers(where: {address: {_regex: $address}}) {
    address
    username
    races_won
    tokens_won
    __typename
  }
}

